import React, { useState } from 'react';
import { usePizza } from '../context/PizzaContext'; // Adjust the path as necessary

const SearchModal = ({ isOpen, onClose }) => {
    const { state, fetchPizzas } = usePizza();
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredPizzas, setFilteredPizzas] = useState([]);

    const handleSearch = () => {
        if (!searchTerm) return;

        // Filter pizzas based on the search term (case insensitive)
        const filtered = state.pizzas.filter(pizza =>
            pizza.Name.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredPizzas(filtered);
    };

    return (
        isOpen && (
            <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
                <div className="bg-white rounded-lg shadow-lg p-6 w-96">
                    <h2 className="text-xl font-bold mb-4">Search Pizzas</h2>
                    <input
                        type="text"
                        placeholder="Search for a pizza..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full p-2 border border-gray-300 rounded mb-4"
                    />
                    <button
                        onClick={() => {
                            handleSearch();
                            fetchPizzas(); // Optional: Fetch pizzas if not already fetched
                        }}
                        className="bg-blue-500 text-white rounded px-4 py-2 mb-4"
                    >
                        Search
                    </button>

                    <button
                        onClick={onClose}
                        className="bg-red-500 text-white rounded px-4 py-2"
                    >
                        Close
                    </button>

                    <div className="mt-4">
                        {filteredPizzas.length > 0 ? (
                            <ul>
                                {filteredPizzas.map((pizza) => (
                                    <li key={pizza.id} className="py-1">
                                        {pizza.Name}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p>No results found</p>
                        )}
                    </div>
                </div>
            </div>
        )
    );
};

export default SearchModal;
