import React, { useState, useEffect } from 'react';
import { usePizza } from '../context/PizzaContext';

const SearchModal = ({ isOpen, onClose }) => {
    const { state, fetchPizzas } = usePizza();
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredPizzas, setFilteredPizzas] = useState([]);

    useEffect(() => {
        if (isOpen) {
            fetchPizzas();
        }
    }, [isOpen, fetchPizzas]);

    useEffect(() => {
        if (searchTerm) {
            const results = state.pizzas.filter(pizza =>
                pizza.Name.toLowerCase().includes(searchTerm.toLowerCase())
            );
            setFilteredPizzas(results);
        } else {
            setFilteredPizzas(state.pizzas);
        }
    }, [searchTerm, state.pizzas]);

    return (
        <div
            className={`fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 transition-opacity ${
                isOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'
            }`}
        >
            <div className="bg-white rounded-lg shadow-lg p-5 max-w-md w-full">
                <h2 className="text-lg font-semibold mb-4">Search Pizzas</h2>
                <input
                    type="text"
                    placeholder="Search for a pizza..."
                    className="border rounded-md p-2 w-full mb-4"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button
                    className="text-red-500 hover:text-red-700 mb-2"
                    onClick={onClose}
                >
                    Close
                </button>
                <div className="max-h-60 overflow-y-auto">
                    {filteredPizzas.length > 0 ? (
                        filteredPizzas.map((pizza) => (
                            <div key={pizza.id} className="p-2 border-b">
                                <h3 className="font-medium">{pizza.Name}</h3>
                                <p className="text-gray-600">{pizza.Description}</p>
                            </div>
                        ))
                    ) : (
                        <p className="text-gray-500">No pizzas found</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default SearchModal;
