import React, { useState, useEffect, useRef } from 'react';
import {
  FaShoppingCart, FaSearch, FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn,
  FaMapMarker, FaArrowRight, FaPlus
} from 'react-icons/fa';
import backgroundImage from '../assets/images/background.png';
import bruger from '../assets/images/b (1).png';
import burger2 from '../assets/images/b (2).png';
import burger3 from '../assets/images/b (5).png';
import logo from '../assets/images/logo.png';
import PizzaCard from './PizzaCard';
import Menubg from '../assets/images/menu-bg.png';
import { usePizza } from '../context/PizzaContext';
import Loading from '../components/Loading';
import Error from './Error';
import Pasta from '../assets/images/pasta.png';
import PastaVideo from '../assets/images/foodVideo.mp4';
import Fastfood1 from '../assets/images/section3(1).png';
import Fastfood2 from '../assets/images/section3(2).png';
import Ceo from '../assets/images/ceo.png';

const Home = () => {
  const [selectedImage, setSelectedImage] = useState(bruger);
  const { state, fetchPizzas } = usePizza();
  const { pizzas, loading, error } = state;

  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showControls, setShowControls] = useState(false);

  const handlePlay = () => {
    videoRef.current.play();
    setIsPlaying(true);
  };

  const handlePause = () => {
    videoRef.current.pause();
    setIsPlaying(false);
  };

  const handleMouseEnter = () => {
    if (isPlaying) {
      setShowControls(true);
    }
  };

  const handleMouseLeave = () => {
    setShowControls(false);
  };

  useEffect(() => {
    fetchPizzas();
  }, [fetchPizzas]);

  if (loading) return <Loading />;
  if (error) return <Error />;

  const imageOptions = [
    { src: bruger, alt: 'Burger 1' },
    { src: burger2, alt: 'Burger 2' },
    { src: burger3, alt: 'Burger 3' },
  ];

  return (
    <div className="relative">
      <section id="main">
        {/* Top bar */}
        <div className="bg-[#e90028] text-white py-2 text-xs md:text-sm flex items-center px-4 md:px-8">
          <div className="flex-grow"></div>
          <div className="text-center">Free Delivery on all orders Over $50</div>
          <div className="flex-grow flex justify-end space-x-2 md:space-x-4 items-center text-xs md:text-sm">
            <span className="hidden md:flex items-center space-x-1">
              <FaMapMarker />
              <span>Rd. Allentown, New Mexico 31134</span>
            </span>
            <div className="flex space-x-2">
              <FaFacebookF className="hover:text-gray-200 cursor-pointer" />
              <FaTwitter className="hover:text-gray-200 cursor-pointer" />
              <FaInstagram className="hover:text-gray-200 cursor-pointer" />
              <FaLinkedinIn className="hover:text-gray-200 cursor-pointer" />
            </div>
          </div>
        </div>

        {/* Navbar */}
        <div className="bg-[#e90028]">
          <nav className="relative bg-[#010f1c] flex items-center justify-between px-4 md:px-8 py-3 md:py-4 text-white">
            <div className="flex items-center">
              <img src={logo} alt="PIZZAN Logo" className="h-10 md:h-14 w-auto" />
            </div>

            <ul className="hidden md:flex space-x-6 md:space-x-8 text-base md:text-lg font-medium">
              <li><a href="#home" className="hover:text-red-600 flex items-center space-x-2"><span>Home</span> <FaPlus className="w-3 h-3" /></a></li>
              <li><a href="#menu" className="hover:text-red-600 flex items-center space-x-2"><span>Menu</span> <FaPlus className="w-3 h-3" /></a></li>
              <li><a href="#shop" className="hover:text-red-600 flex items-center space-x-2"><span>Shop</span> <FaPlus className="w-3 h-3" /></a></li>
              <li><a href="#pages" className="hover:text-red-600 flex items-center space-x-2"><span>Pages</span> <FaPlus className="w-3 h-3" /></a></li>
              <li><a href="#news" className="hover:text-red-600 flex items-center space-x-2"><span>News</span> <FaPlus className="w-3 h-3" /></a></li>
              <li><a href="#contact" className="hover:text-red-600 flex items-center space-x-2"><span>Contact Us</span></a></li>
            </ul>

            <div className="flex items-center space-x-4 md:space-x-6">
              <FaSearch className="text-xl md:text-2xl cursor-pointer hover:text-yellow-500" />
              <FaShoppingCart className="text-xl md:text-2xl cursor-pointer hover:text-yellow-500" />
              <button className="bg-[#e90028] flex items-center justify-center space-x-2 text-white px-3 md:px-4 py-2 rounded-sm font-semibold hover:bg-red-700">
                <span>BOOK A TABLE</span> <FaArrowRight />
              </button>
            </div>
          </nav>
        </div>

        {/* Main section */}
        <div className="px-10 relative bg-cover bg-center h-[50vh] md:h-[90vh] flex items-center" style={{ backgroundImage: `url(${backgroundImage})` }}>
          <div className="absolute inset-0 bg-black bg-opacity-50"></div>
          <div className="relative ml-24 z-10 px-4 md:px-8 text-white max-w-md md:max-w-lg">
            <p className="font-galada text-[#e90028] mb-4 text-xl">Welcome to PIZZAN </p>
            <h1 className="text-3xl md:text-5xl font-customH1 font-bold mb-4">GET BEST QUALITY FOOD FROM US</h1>
            <button className="flex items-center justify-center space-x-2 bg-[#e90028] text-white px-4 md:px-6 py-2 md:py-3 rounded-sm font-semibold hover:bg-red-700">
              <span>EXPLORE MENU</span> <FaArrowRight className="w-4 h-4" />
            </button>
          </div>

          <div className="absolute left-0 ml-8 space-y-4">
            {imageOptions.map((image, index) => (
              <img key={index} src={image.src} alt={image.alt} className={`w-10 h-10 cursor-pointer border-2 ${selectedImage === image.src ? 'border-red-500' : 'border-transparent'} rounded`} onClick={() => setSelectedImage(image.src)} />
            ))}
          </div>

          <div className="relative z-10 w-full ml-56 hidden md:block items-center">
            <img src={selectedImage} alt="Food Illustration" className="h-80 md:h-[30rem] w-auto" />
          </div>
        </div>
      </section>

      {/* Menu Section */}
      <section id="menu" style={{ backgroundImage: `url(${Menubg})` }} className="bg-cover bg-center relative">
        <div className="absolute inset-0 bg-white opacity-50"></div>
        <div className="py-24 px-10 relative z-10">
          <div className="flex flex-col justify-center items-center text-center">
            <p className="font-galada text-[#e90028] text-2xl">Best Food Menu</p>
            <h1 className="font-bold text-4xl mt-2 mb-10">Our Popular Food Items</h1>
          </div>

          {/* Pizza Cards */}
          <div className="px-4 md:px-20 grid grid-cols-2 md:grid-cols-4 gap-6 p-6">
            {pizzas.map(pizza => (
              <PizzaCard
                key={pizza.id}
                image={pizza.Image}
                name={pizza.Name}
                description={pizza.Description}
                price={pizza.Price}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Video Section */}
      <section>
        <div className="flex flex-col md:flex-row items-center gap-10 pr-8">
          <div className="relative w-full md:w-3/5 px-8 md:px-20 py-12">
            <img src={Pasta} alt="Pasta Dish" className="w-96 rounded-sm" />
            <div className="absolute top-1/2 right-20 md:right-60 transform -translate-y-1/2 z-10">
              <button className="bg-white text-red-700 rounded-full p-2 text-sm font-semibold shadow-md hover:bg-red-500 hover:text-white transition ease-in-out duration-150">ORDER NOW</button>
            </div>
          </div>

          <div className="w-full md:w-2/5 px-8 md:px-20 relative">
            <div className="relative">
              <video
                ref={videoRef}
                className="w-full h-full"
                src={PastaVideo}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                controls={showControls}
              />
              <div className="absolute inset-0 flex items-center justify-center">
                {isPlaying ? (
                  <button
                    className="bg-white rounded-full p-2 shadow-md"
                    onClick={handlePause}
                  >
                    Pause
                  </button>
                ) : (
                  <button
                    className="bg-white rounded-full p-2 shadow-md"
                    onClick={handlePlay}
                  >
                    Play
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
