import React, { useState, useEffect } from 'react';
import { getReservation } from '../services/reservationService'; // Import the getReservation function
import Loading from '../components/Loading';
import Error from './Error';

const ManageReservation = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true); // State to handle loading
  const [error, setError] = useState(null); // State to handle errors

  // Function to fetch reservations from the server
  const fetchReservations = async () => {
    const token = localStorage.getItem('token'); // Get the JWT token from local storage
    try {
      console.log(token)
      const data = await getReservation({}, token); // Call the service function to get reservations
      setReservations(data); // Update reservations with the fetched data
      setLoading(false); // Stop loading once data is fetched
    } catch (error) {
      setError('Error fetching reservations'); // Set error if there's an issue
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReservations(); // Fetch reservations when the component mounts
  }, []);

  const updateStatus = (id, newStatus) => {
    setReservations(
      reservations.map((reservation) =>
        reservation.id === id
          ? { ...reservation, status: newStatus }
          : reservation
      )
    );
  };

  const handleDelete = (id) => {
    setReservations(reservations.filter((reservation) => reservation.id !== id));
  };

  if (loading) return <Loading />;
  if (error) return <Error />;

  return (
    <div>
      <h2 className="text-3xl mb-4">Manage Reservations</h2>

      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Customer Name</th>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Reservation Date</th>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Number of People</th>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Contact Number</th>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Status</th>
            <th className="py-2 px-4 bg-gray-200 text-gray-600 font-bold">Actions</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((reservation) => (
            <tr key={reservation.id} className="border-b">
              <td className="py-2 px-4">{reservation.CustomerName}</td>
              <td className="py-2 px-4">{reservation.ReservationDate}</td>
              <td className="py-2 px-4">{reservation.NumberOfPeople}</td>
              <td className="py-2 px-4">{reservation.ContactNumber}</td>
              <td className="py-2 px-4">{reservation.Status}</td>
              <td className="py-2 px-4 space-x-2">
                <button
                  onClick={() => updateStatus(reservation.id, 'Confirmed')}
                  className="bg-green-500 text-white p-1 rounded"
                >
                  Confirm
                </button>
                <button
                  onClick={() => updateStatus(reservation.id, 'Cancelled')}
                  className="bg-yellow-500 text-white p-1 rounded"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleDelete(reservation.id)}
                  className="bg-red-500 text-white p-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageReservation;
