// SearchModal.jsx
import React, { useState } from 'react';
import { FaTimes } from 'react-icons/fa';
import { usePizza } from '../context/PizzaContextizzaContext'; // Adjust the import according to your context file location

const SearchModal = ({ isOpen, onClose }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const { fetchPizzas, filterPizzas, state } = usePizza();

    // Fetch all pizzas when modal is opened
    React.useEffect(() => {
        if (isOpen) {
            fetchPizzas(); // Fetch pizzas only if modal is open
        }
    }, [isOpen, fetchPizzas]);

    const handleSearch = async (e) => {
        e.preventDefault();
        // Filter pizzas based on searchTerm
        filterPizzas(searchTerm);
        // Optionally close the modal after search
        onClose();
    };

    return (
        isOpen && (
            <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
                <div className="bg-white rounded-lg p-6 w-1/3 shadow-lg">
                    <div className="flex justify-between items-center">
                        <h2 className="text-xl font-semibold">Search Pizzas</h2>
                        <button onClick={onClose}>
                            <FaTimes className="text-gray-500 hover:text-gray-700" />
                        </button>
                    </div>
                    <form onSubmit={handleSearch} className="mt-4">
                        <input
                            type="text"
                            placeholder="Search for pizzas..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="border rounded-md p-2 w-full focus:outline-none focus:ring-2 focus:ring-red-500"
                        />
                        <button
                            type="submit"
                            className="mt-4 w-full bg-red-500 text-white rounded-md p-2 hover:bg-red-600"
                        >
                            Search
                        </button>
                    </form>

                    {/* Render filtered pizzas here */}
                    <div className="mt-4">
                        <h3 className="font-semibold">Search Results:</h3>
                        {state.filteredPizzas.length > 0 ? (
                            <ul>
                                {state.filteredPizzas.map(pizza => (
                                    <li key={pizza.id} className="py-1">{pizza.name}</li> // Adjust based on your pizza object structure
                                ))}
                            </ul>
                        ) : (
                            <p>No pizzas found.</p>
                        )}
                    </div>
                </div>
            </div>
        )
    );
};

export default SearchModal;
