import React, { useState } from 'react';
import { FaArrowRight } from 'react-icons/fa';
import { addReservation } from '../services/reservationService'; 
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../assets/styles/common.css';


const Reservation = () => {
    const [ReservationDate, setDate] = useState(null); // Set initial date as null
    const [ReservationTime, setTime] = useState('');
    const [NumberOfPeople, setGuests] = useState('');
    const [ContactNumber, setContactNumber] = useState('');
    const [ContactName, setContactName] = useState('');
    const [Message, setMessage] = useState('');

    const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Prepare the data object to send
        const reservationData = {
            CustomerName: ContactName,
            ContactNumber,
            ReservationDate,
            ReservationTime,
            NumberOfPeople,
            Message,
        };

        try {
          // Call the addReservation service function to save the reservation
          const response = await addReservation(reservationData);
          console.log('Reservation successfully made:', response);

          // Reset form fields
          setDate(null);
          setTime('');
          setGuests('');
          setContactName('');
          setContactNumber('');
          setMessage('');
          alert('Reservation successfully made!'); // Optionally add success feedback
      } catch (error) {
          console.error('Error making reservation:', error);
          alert('There was an error making the reservation. Please try again.');
      }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Date and Time Fields */}
            <div className="mb-4 flex space-x-4">
                <input
                    type="text"
                    id="contactName"
                    value={ContactName}
                    onChange={(e) => setContactName(e.target.value)}
                    className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                    required
                    placeholder="Contact Name"
                />
                <input
                    type="tel"
                    id="contactNumber"
                    value={ContactNumber}
                    onChange={(e) => setContactNumber(e.target.value)}
                    className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                    required
                    placeholder="Contact Number"
                />
            </div>

            {/* Reservation Date, Time, and Guests Fields */}
            <div className="mb-4 flex space-x-4">
                <div className="flex-1">
                    <input
                        type="date"
                        id="date"
                        value={ReservationDate}
                        onChange={(e) => setDate(e.target.value)}
                        className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                        required
                    />
                </div>
                <div className="flex-1">
                    <input
                        type="time"
                        id="time"
                        value={ReservationTime}
                        onChange={(e) => setTime(e.target.value)}
                        className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                        required
                    />
                </div>
                <div className="flex-1">
                    <input
                        type="number"
                        id="guests"
                        value={NumberOfPeople}
                        onChange={(e) => setGuests(e.target.value)}
                        className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                        required
                        min="1"
                        placeholder="Total Guests"
                    />
                </div>
            </div>

            {/* Message Field */}
            <div className="mb-4">
                <textarea
                    id="message"
                    value={Message}
                    onChange={(e) => setMessage(e.target.value)}
                    className="mt-1 block w-full h-32 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
                    placeholder="Write Message"
                ></textarea>
            </div>

            {/* Submit Button */}
            <button
                type="submit"
                className="bg-[#e90028] flex items-center justify-center space-x-2 text-white px-10 md:px-4 py-5 rounded-sm font-semibold hover:bg-red-700"
            >
                <span>Make A Reservation</span>
                <FaArrowRight className="w-3 sm:w-4 h-3 sm:h-4" />
            </button>
        </form>
    );
};

export default Reservation;
