import { usePizza } from '../context/PizzaContext';
import Loading from '../components/Loading';
import Error from './Error';
import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash } from 'react-icons/fa'; // Import icons
import Swal from 'sweetalert2';
import { ToastContainer, toast } from 'react-toastify';
import { addPizza, updatePizza, deletePizza } from '../services/pizzaService'; 



const ManagePizza = () => {
    const { state, fetchPizzas, addPizza, handleEdit } = usePizza();
    const { pizzas, loading, error } = state;

    const [newPizza, setNewPizza] = useState({ name: '', price: '', description: '', image: '' });
    const [isModalOpen, setIsModalOpen] = useState(false); // Manage modal visibility
    const [editingPizzaId, setEditingPizzaId] = useState(null); // Track pizza being edited

    useEffect(() => {
        fetchPizzas();
    }, [fetchPizzas]);

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => setIsModalOpen(false);

    if (loading) return <Loading />;
    if (error) return <Error />;


    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: 'Are you sure?',
            text: 'You won\'t be able to revert this!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#e90028',
            cancelButtonColor: '#535361',
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'Cancel',
        });

        if (result.isConfirmed) {
            toast.success('Pizza deleted successfully!');


        } else {
            toast.info('Pizza deletion unsuccessfully.');

        }
    };

    return (
        <div className="p-4">
            <h2 className="text-3xl font-semibold mb-4">Manage Pizza</h2>

            {/* Add New Pizza Button */}
            <div className="flex justify-end mb-6"> {/* Flex container to align the button to the right */}
                <button onClick={openModal} className="bg-[#e90028] hover:bg-red-700 text-white py-2 px-4 rounded">
                    Add New Pizza
                </button>
            </div>


            {/* Pizza Table */}
            <div>
                <div className="overflow-x-auto">
                    <table className="min-w-full bg-white border">
                        <thead>
                            <tr className="bg-gray-100 text-left">
                                <th className="py-2 px-4 border-b">Pizza Name</th>
                                <th className="py-2 px-4 border-b">Price</th>
                                <th className="py-2 px-4 border-b">Description</th>
                                <th className="py-2 px-4 border-b">Image</th>
                                <th className="py-2 px-4 border-b">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {pizzas.map((pizza) => (
                                <tr key={pizza.id} className="border-b">
                                    <td className="py-2 px-4">{pizza.Name}</td>
                                    <td className="py-2 px-4">${pizza.Price}</td>
                                    <td className="py-2 px-4">{pizza.Description}</td>
                                    <td className="py-2 px-4">
                                        <img src={pizza.Image} alt={pizza.Name} className="w-16 h-16 object-cover" />
                                    </td>
                                    <td className=" justify-normal py-2 px-4 flex space-x-2">
                                        <button onClick={() => handleEdit(pizza.id)} className="text-black text-3xl">
                                            <FaEdit />
                                        </button>
                                        <button onClick={() => handleDelete(pizza.id)} className="text-[#e90028] text-2xl">
                                            <FaTrash />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Modal Popup for Adding New Pizza */}
            {isModalOpen && (
                <div
                    className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
                    onClick={closeModal} // Close modal on background click
                >
                    <div
                        className="bg-white rounded-lg shadow-lg p-8 w-3/4"
                        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside modal
                    >
                        <h3 className="text-3xl font-semibold mb-4 text-start">Add New Pizza</h3>

                        <div className="space-y-4">
                            <div className=" flex space-x-4">
                                <div className="flex-1">
                                    <input
                                        className="border w-full p-2 rounded"
                                        type="text"
                                        placeholder="Pizza Name"
                                        value={newPizza.name}
                                        onChange={(e) => setNewPizza({ ...newPizza, name: e.target.value })}
                                    />
                                </div>
                                <div className="flex-1">
                                    <input
                                        className="border w-full p-2 rounded"
                                        type="number"
                                        placeholder="Price"
                                        value={newPizza.price}
                                        onChange={(e) => setNewPizza({ ...newPizza, price: e.target.value })}
                                    />
                                </div>
                            </div>
                            <input
                                className="border w-full p-2 rounded"
                                type="file"
                                placeholder="Image URL"
                                value={newPizza.image}
                                onChange={(e) => setNewPizza({ ...newPizza, image: e.target.value })}
                            />
                            <textarea
                                className="border w-full h-[20vh] p-2 rounded"
                                type="text"
                                placeholder="Description"
                                value={newPizza.description}
                                onChange={(e) => setNewPizza({ ...newPizza, description: e.target.value })}
                            />

                        </div>

                        <div className="flex justify-end space-x-2 mt-6">
                            <button onClick={closeModal} className="bg-gray-100 hover:bg-gray-200 text-black py-2 px-4 rounded">
                                Cancel
                            </button>
                            <button
                                onClick={() => {
                                    addPizza(newPizza);
                                    closeModal();
                                }}
                                className="bg-[#e90028] hover:bg-red-700 text-white py-2 px-4 rounded"
                            >
                                Add Pizza
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManagePizza;
