import React, { useState } from 'react';
import { FaArrowRight } from 'react-icons/fa';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../assets/styles/common.css';

const Reservation = () => {
    const [ReservationDate, setDate] = useState(null); // Set initial date as null
    const [ReservationTime, setTime] = useState('');
    const [guests, setGuests] = useState('');
    const [contactNumber, setContactNumber] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        setDate(null);
        setTime('');
        setGuests('');
        setContactNumber('');
        setMessage('');
    };

    return (
        <form onSubmit={handleSubmit}>
        {/* Date and Time Fields */}
        <div className="mb-4 flex space-x-4">
        <input
              type="tel"
              id="contact"
              value={ContactName}
              onChange={(e) => setContactNumber(e.target.value)}
              className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
              required
              placeholder="Contact Number"
            />
             <input
              type="number"
              id="contact"
              value={contactNumber}
              onChange={(e) => setContactNumber(e.target.value)}
              className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
              required
              placeholder="Contact Number"
            />
          
        </div>
      
        {/* Contact and Guests Fields */}
        <div className="mb-4 flex space-x-3">
          <div className="flex-1">
          <div className="flex-1">
            <input
              type="date"
              id="date"
              value={ReservationDate}
              onChange={(e) => setDate(e.target.value)}
              className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
              required
              placeholder="Select Date"
            />
          </div>
          <div className="flex-1">
            <input
              type="time"
              id="time"
              value={ReservationTime}
              onChange={(e) => setTime(e.target.value)}
              className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
              required
              placeholder="Select Time"
            />
          </div>
          </div>
          <div className="flex-1">
            <input
              type="number"
              id="guests"
              value={NumberOfPeople}
              onChange={(e) => setGuests(e.target.value)}
              className="mt-1 block w-full h-12 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
              required
              min="1"
              placeholder="Total Guests"
            />
          </div>
        </div>
      
        {/* Message Field */}
        <div className="mb-4">
          <textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="mt-1 block w-full h-32 p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 placeholder-gray-500"
            placeholder="Write Message"
          ></textarea>
        </div>
      
        {/* Submit Button */}
        <button
          type="submit"
          className="bg-[#e90028] flex items-center justify-center space-x-2 text-white px-10 md:px-4 py-5 rounded-sm font-semibold hover:bg-red-700"
        >
          <span>Make A Reservation</span>
          <FaArrowRight className="w-3 sm:w-4 h-3 sm:h-4" />
        </button>
      </form>
      
      
    );
};

export default Reservation;
