import React, { useState, useEffect } from 'react';
import { usePizza } from '../context/PizzaContext';
import { getReservation, updateReservationStatus } from '../services/reservationService'; // Import the required functions

import Loading from '../components/Loading';
import Error from './Error';

const Dashboard = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

  const { state: { pizzas }, fetchPizzas } = usePizza(); // Access pizzas from state
  // Function to fetch reservations from the server
  const fetchReservations = async () => {
    const token = localStorage.getItem('token'); // Get the JWT token from local storage
    try {
      const data = await getReservation({}, token); // Call the service function to get reservations
      setReservations(data); // Update reservations with the fetched data
      setLoading(false); // Stop loading once data is fetched
    } catch (error) {
      setError('Error fetching reservations'); // Set error if there's an issue
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReservations(); // Fetch reservations when the component mounts
  }, []);

  useEffect(() => {
    fetchPizzas(); // Fetch pizzas when the component mounts
  }, [fetchPizzas]);

  const today = new Date().toISOString().split('T')[0];
  const todaysReservations = reservations.filter(
    (reservation) => reservation.date === today
  );
  const pendingReservations = reservations.filter(
    (reservation) => reservation.status === 'Pending'
  ).length;

  if (Array.isArray(pizzas)) {
    console.log('Pizzas array length:', pizzas.length);
  } else {
    console.log('Pizzas is not an array:', pizzas);
  }

  if (loading) return <Loading />;
  if (error) return <Error />;


  return (
    <div>
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      {/* Summary Boxes */}
      <div className="grid grid-cols-2 gap-4 mb-8">
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Total Pizza Menu</h3>
          <p className="text-4xl">{pizzas ? pizzas.length : 0}</p> {/* Safely access pizzas length */}
        </div>
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Pending Reservations</h3>
          <p className="text-4xl">{pendingReservations}</p>
        </div>
      </div>

      {/* Today's Reservations Table */}
      <div className="bg-white p-6 shadow rounded-lg">
        <h3 className="text-xl font-semibold mb-4">Today's Reservations</h3>
        <table className="w-full text-left border-collapse">
          <thead>
            <tr>
              <th className="border-b p-2">Name</th>
              <th className="border-b p-2">Date</th>
              <th className="border-b p-2">Status</th>
            </tr>
          </thead>
          <tbody>
            {todaysReservations.map((reservation) => (
              <tr key={reservation.id}>
                <td className="border-b p-2">{reservation.name}</td>
                <td className="border-b p-2">{reservation.date}</td>
                <td className="border-b p-2">{reservation.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
