import { usePizza } from '../context/PizzaContext';
import Loading from '../components/Loading';
import Error from './Error';
import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash } from 'react-icons/fa';

const ManagePizza = () => {
  const { state, fetchPizzas, addPizza, handleDelete, handleEdit } = usePizza();
  const { pizzas, loading, error } = state;

  const [newPizza, setNewPizza] = useState({ name: '', price: '', description: '', image: '' });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isConfirmDeleteOpen, setIsConfirmDeleteOpen] = useState(false);
  const [pizzaToDelete, setPizzaToDelete] = useState(null); // Store the pizza ID to delete

  useEffect(() => {
    fetchPizzas();
  }, [fetchPizzas]);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const openConfirmDelete = (id) => {
    setPizzaToDelete(id); // Set the pizza ID to delete
    setIsConfirmDeleteOpen(true);
  };

  const closeConfirmDelete = () => {
    setIsConfirmDeleteOpen(false);
    setPizzaToDelete(null); // Clear the pizza ID
  };

  const confirmDelete = () => {
    handleDelete(pizzaToDelete);
    closeConfirmDelete(); // Close the confirmation modal
  };

  if (loading) return <Loading />;
  if (error) return <Error />;

  return (
    <div className="p-4">
      <h2 className="text-3xl mb-4">Manage Pizza</h2>

      {/* Add New Pizza Button */}
      <div className="flex justify-end mb-6">
        <button onClick={openModal} className="bg-[#e90028] text-white py-2 px-4 rounded">
          Add New Pizza
        </button>
      </div>

      {/* Pizza Table */}
      <div>
        <h3 className="text-xl mb-2">Pizza List</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100 text-left">
                <th className="py-2 px-4 border-b">Pizza Name</th>
                <th className="py-2 px-4 border-b">Price</th>
                <th className="py-2 px-4 border-b">Description</th>
                <th className="py-2 px-4 border-b">Image</th>
                <th className="py-2 px-4 border-b">Actions</th>
              </tr>
            </thead>
            <tbody>
              {pizzas.map((pizza) => (
                <tr key={pizza.id} className="border-b">
                  <td className="py-2 px-4">{pizza.Name}</td>
                  <td className="py-2 px-4">${pizza.Price}</td>
                  <td className="py-2 px-4">{pizza.Description}</td>
                  <td className="py-2 px-4">
                    <img src={pizza.Image} alt={pizza.Name} className="w-16 h-16 object-cover" />
                  </td>
                  <td className="justify-normal py-2 px-4 flex space-x-2">
                    <button onClick={() => handleEdit(pizza.id)} className="text-black text-3xl">
                      <FaEdit />
                    </button>
                    <button onClick={() => openConfirmDelete(pizza.id)} className="text-[#e90028] text-2xl">
                      <FaTrash />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal Popup for Adding New Pizza */}
      {isModalOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          onClick={closeModal}
        >
          <div
            className="bg-white rounded-lg shadow-lg p-8 w-11/12 md:w-1/2 lg:w-1/3"
            onClick={(e) => e.stopPropagation()}
          >
            <h3 className="text-xl mb-4 text-center">Add New Pizza</h3>
            <div className="space-y-4">
              <input
                className="border w-full p-2 rounded"
                type="text"
                placeholder="Pizza Name"
                value={newPizza.name}
                onChange={(e) => setNewPizza({ ...newPizza, name: e.target.value })}
              />
              <input
                className="border w-full p-2 rounded"
                type="number"
                placeholder="Price"
                value={newPizza.price}
                onChange={(e) => setNewPizza({ ...newPizza, price: e.target.value })}
              />
              <input
                className="border w-full p-2 rounded"
                type="text"
                placeholder="Description"
                value={newPizza.description}
                onChange={(e) => setNewPizza({ ...newPizza, description: e.target.value })}
              />
              <input
                className="border w-full p-2 rounded"
                type="text"
                placeholder="Image URL"
                value={newPizza.image}
                onChange={(e) => setNewPizza({ ...newPizza, image: e.target.value })}
              />
            </div>
            <div className="flex justify-end space-x-2 mt-6">
              <button onClick={closeModal} className="bg-gray-500 text-white py-2 px-4 rounded">
                Cancel
              </button>
              <button
                onClick={() => {
                  addPizza(newPizza);
                  closeModal();
                }}
                className="bg-green-500 text-white py-2 px-4 rounded"
              >
                Add Pizza
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Confirmation Modal for Deletion */}
      {isConfirmDeleteOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          onClick={closeConfirmDelete}
        >
          <div
            className="bg-white rounded-lg shadow-lg p-8 w-11/12 md:w-1/3 lg:w-1/4"
            onClick={(e) => e.stopPropagation()}
          >
            <h3 className="text-xl mb-4 text-center">Confirm Deletion</h3>
            <p className="mb-4 text-center">Are you sure you want to delete this pizza?</p>
            <div className="flex justify-between">
              <button onClick={closeConfirmDelete} className="bg-gray-500 text-white py-2 px-4 rounded">
                Cancel
              </button>
              <button onClick={confirmDelete} className="bg-red-500 text-white py-2 px-4 rounded">
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManagePizza;
