import React, { useState } from 'react';
import { FaArrowRight } from 'react-icons/fa';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import '../assets/styles/common.css';

const Reservation = () => {
    const [date, setDate] = useState(null); // Set initial date as null
    const [time, setTime] = useState('');
    const [guests, setGuests] = useState(1);
    const [contactNumber, setContactNumber] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log({ date, time, guests, contactNumber, message });
        setDate(null);
        setTime('');
        setGuests(1);
        setContactNumber('');
        setMessage('');
    };

    return (
        <form onSubmit={handleSubmit}>
        <div className="mb-4 flex space-x-4">
          <div className="flex-1">
          <DatePicker
            selected={date}
            onChange={(date) => setDate(date)}
            dateFormat="dd/MM/yyyy"
            placeholderText="Select Date"
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-[#e90028]"
            calendarClassName="custom-calendar" // Apply custom calendar color
            required
          />
          </div>
          <div className="flex-1">
            <input
              type="time"
              id="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
              required
              placeholder="Select Time"
            />
          </div>
        </div>
        <div className="mb-4 flex space-x-4">
          <div className="flex-1">
            <input
              type="tel"
              id="contact"
              value={contactNumber}
              onChange={(e) => setContactNumber(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
              required
              placeholder="Contact Number"
            />
          </div>
          <div className="flex-1">
            <input
              type="number"
              id="guests"
              value={guests}
              onChange={(e) => setGuests(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
              required
              min="1"
              placeholder="Total Guests"
            />
          </div>
        </div>
        <div className="mb-4">
          <textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
            rows="3"
          ></textarea>
        </div>
        <button
          type="submit"
          className="bg-[#e90028] flex items-center justify-center space-x-2 text-white px-10 md:px-4 py-5 rounded-sm font-semibold hover:bg-red-700"
        >
          <span>Make A Reservation</span>
          <FaArrowRight className="w-3 sm:w-4 h-3 sm:h-4" />
        </button>
      </form>
      
    );
};

export default Reservation;
