import React, { useState } from 'react';
import ManagePizza from './ManagePizza';
import ManageReservation from './ManageReservation';

const AdminPanel = () => {
  const [activeSection, setActiveSection] = useState('pizza');

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-1/4 bg-gray-800 text-white p-5">
        <h2 className="text-2xl font-bold mb-8">Admin Panel</h2>
        <ul>
          <li>
            <button
              className={`w-full text-left p-3 mb-2 rounded ${
                activeSection === 'pizza' ? 'bg-red-500' : ''
              }`}
              onClick={() => setActiveSection('pizza')}
            >
              Manage Pizza
            </button>
          </li>
          <li>
            <button
              className={`w-full text-left p-3 mb-2 rounded ${
                activeSection === 'reservations' ? 'bg-red-500' : ''
              }`}
              onClick={() => setActiveSection('reservations')}
            >
              Manage Reservations
            </button>
          </li>
        </ul>
      </aside>

      {/* Content Area */}
      <main className="w-3/4 p-10 bg-gray-100">
        {activeSection === 'pizza' && <ManagePizza />}
        {activeSection === 'reservations' && <ManageReservation />}
      </main>
    </div>
  );
};

export default AdminPanel;
