import React, { useState, useEffect } from 'react';
import { usePizza } from '../context/PizzaContext';
import { getReservation } from '../services/reservationService';

import Loading from '../components/Loading';
import Error from './Error';

const Dashboard = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

  const { state: { pizzas }, fetchPizzas } = usePizza(); 

  const fetchReservations = async () => {
    const token = localStorage.getItem('token');
    try {
      const data = await getReservation({}, token);
      setReservations(data);
      setLoading(false);
    } catch (error) {
      setError('Error fetching reservations');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  useEffect(() => {
    fetchPizzas();
  }, [fetchPizzas]);

  const today = new Date().toISOString().split('T')[0];
  const todaysReservations = reservations.filter(
    (reservation) => reservation.date === today
  );
  const pendingReservations = reservations.filter(
    (reservation) => reservation.status === 'Pending'
  ).length;

  if (loading) return <Loading />;
  if (error) return <Error />;

  return (
    <div>
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      {/* Summary Boxes */}
      <div className="grid grid-cols-2 gap-4 mb-8">
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Total Pizza Menu</h3>
          <p className="text-4xl">{Array.isArray(pizzas) ? pizzas.length : 0}</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Pending Reservations</h3>
          <p className="text-4xl">{pendingReservations}</p>
        </div>
      </div>

      {/* Today's Reservations Table */}
      <div className="bg-white p-6 shadow rounded-lg">
        <h3 className="text-xl font-semibold mb-4">Today's Reservations</h3>
        <table className="w-full text-left border-collapse">
          <thead>
            <tr>
              <th className="border-b p-2">Name</th>
              <th className="border-b p-2">Contact Number</th>

              <th className="border-b p-2">Date</th>
              <th className="border-b p-2">Time</th>
              <th className="border-b p-2">Number of Guests</th>
              <th className="border-b p-2">Status</th>
            </tr>
          </thead>
          <tbody>
            {todaysReservations.map((reservation) => (
              <tr key={reservation.id}>
                <td className="border-b p-2">{reservation.CustomerName}</td> {/* Adjusted to 'name' */}
                <td className="border-b p-2">{reservation.CustomerName}</td> {/* Adjusted to 'name' */}

                <td className="border-b p-2">{reservation.ReservationDate}</td>
                <td className="border-b p-2">{reservation.ReservationTime}</td> {/* Assuming 'time' is a property */}
                <td className="border-b p-2">{reservation.NumberOfPeople}</td> {/* Assuming 'guests' is a property */}
                <td className="border-b p-2">{reservation.Status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
