import React, { useState, useEffect } from 'react';
import { usePizza } from '../context/PizzaContext';
import Loading from '../components/Loading';
import Error from './Error';
import { FaEdit, FaTrash } from 'react-icons/fa';
import Swal from 'sweetalert2';
import { ToastContainer, toast } from 'react-toastify';
import { addPizza, updatePizza, deletePizza } from '../services/pizzaService'; // Import your service functions

const ManagePizza = () => {
    const { state, fetchPizzas } = usePizza();
    const { pizzas, loading, error } = state;

    const [newPizza, setNewPizza] = useState({ Name: '', Price: '', Description: '', Image: '' });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingPizzaId, setEditingPizzaId] = useState(null);

    useEffect(() => {
        fetchPizzas();
    }, [fetchPizzas]);

    const openModal = (pizza = null) => {
        if (pizza) {
            setNewPizza({ Name: pizza.Name, Price: pizza.Price, Description: pizza.Description, Image: '' });
            setEditingPizzaId(pizza.id);
        } else {
            setNewPizza({ Name: '', Price: '', Description: '', Image: '' });
            setEditingPizzaId(null);
        }
        setIsModalOpen(true);
    };

    const closeModal = () => setIsModalOpen(false);

    if (loading) return <Loading />;
    if (error) return <Error />;

    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: 'Are you sure?',
            text: 'You won\'t be able to revert this!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#e90028',
            cancelButtonColor: '#535361',
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'Cancel',
        });

        if (result.isConfirmed) {
            try {
                await deletePizza(id);
                fetchPizzas();
                toast.success('Pizza deleted successfully!');
            } catch (error) {
                toast.error('Failed to delete pizza.');
            }
        } else {
            toast.info('Pizza deletion canceled.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior

        const formData = new FormData();
        formData.append('Name', newPizza.Name);
        formData.append('Price', newPizza.Price);
        formData.append('Description', newPizza.Description);
        if (newPizza.Image) {
            formData.append('Image', newPizza.Image); // Append the image file
        }

        try {
            if (editingPizzaId) {
                await updatePizza(editingPizzaId, formData); // Call update service with FormData
                toast.success('Pizza updated successfully!');
            } else {
                await addPizza(formData); // Call add service with FormData
                toast.success('Pizza added successfully!');
            }
            fetchPizzas();
            closeModal();
        } catch (error) {
            toast.error('Failed to save pizza.');
        }
    };

    return (
        <div className="p-4">
            <h2 className="text-3xl font-semibold mb-4">Manage Pizza</h2>

            <div className="flex justify-end mb-6">
                <button onClick={() => openModal()} className="bg-[#e90028] hover:bg-red-700 text-white py-2 px-4 rounded">
                    Add New Pizza
                </button>
            </div>

            <div className="overflow-x-auto">
                <table className="min-w-full bg-white border">
                    <thead>
                        <tr className="bg-gray-100 text-left">
                            <th className="py-2 px-4 border-b">Pizza Name</th>
                            <th className="py-2 px-4 border-b">Price</th>
                            <th className="py-2 px-4 border-b">Description</th>
                            <th className="py-2 px-4 border-b">Image</th>
                            <th className="py-2 px-4 border-b">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pizzas.map((pizza) => (
                            <tr key={pizza.id} className="border-b">
                                <td className="py-2 px-4">{pizza.Name}</td>
                                <td className="py-2 px-4">${pizza.Price}</td>
                                <td className="py-2 px-4">{pizza.Description}</td>
                                <td className="py-2 px-4">
                                    <img src={pizza.Image} alt={pizza.Name} className="w-16 h-16 object-cover" />
                                </td>
                                <td className="justify-normal py-2 px-4 flex space-x-2">
                                    <button onClick={() => openModal(pizza)} className="text-black text-3xl">
                                        <FaEdit />
                                    </button>
                                    <button onClick={() => handleDelete(pizza.id)} className="text-[#e90028] text-2xl">
                                        <FaTrash />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {isModalOpen && (
                <div
                    className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
                    onClick={closeModal}
                >
                    <div
                        className="bg-white rounded-lg shadow-lg p-8 w-3/4"
                        onClick={(e) => e.stopPropagation()}
                    >
                        <h3 className="text-3xl font-semibold mb-4 text-start">{editingPizzaId ? 'Edit Pizza' : 'Add New Pizza'}</h3>
                        <form onSubmit={handleSubmit}>
                            <div className="space-y-4">
                                <div className="flex space-x-4">
                                    <div className="flex-1">
                                        <input
                                            className="border w-full p-2 rounded"
                                            type="text"
                                            placeholder="Pizza Name"
                                            value={newPizza.Name}
                                            onChange={(e) => setNewPizza({ ...newPizza, Name: e.target.value })}
                                        />
                                    </div>
                                    <div className="flex-1">
                                        <input
                                            className="border w-full p-2 rounded"
                                            type="number"
                                            placeholder="Price"
                                            value={newPizza.Price}
                                            onChange={(e) => setNewPizza({ ...newPizza, Price: e.target.value })}
                                        />
                                    </div>
                                </div>
                                <input
                                    className="border w-full p-2 rounded"
                                    type="file"
                                    onChange={(e) => setNewPizza({ ...newPizza, Image: e.target.files[0] })} // Handle file input
                                />
                                <textarea
                                    className="border w-full h-[20vh] p-2 rounded"
                                    placeholder="Description"
                                    value={newPizza.Description}
                                    onChange={(e) => setNewPizza({ ...newPizza, Description: e.target.value })}
                                />
                            </div>
                            <div className="flex justify-end space-x-2 mt-6">
                                <button type="button" onClick={closeModal} className="bg-gray-100 hover:bg-gray-200 text-black py-2 px-4 rounded">
                                    Cancel
                                </button>
                                <button type="submit" className="bg-[#e90028] hover:bg-red-700 text-white py-2 px-4 rounded">
                                    {editingPizzaId ? 'Update Pizza' : 'Add Pizza'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
            <ToastContainer />
        </div>
    );
};

export default ManagePizza;
