import React from 'react';

const PizzaCard = ({ image, name, description, price }) => {
  return (
    <div className="bg-[#faf7f2] rounded-lg shadow-lg p-6 flex flex-col items-center space-y-4 h-[380px] sm:h-[230px] md:h-[200px] lg:h-[250px] w-full sm:w-[150px] md:w-[200px] lg:w-[250px]">
      {/* Pizza Image */}
      <img 
        src={image} 
        alt={name} 
        className="w-full h-[150px] rounded-lg object-cover" 
      />
      
      {/* Pizza Name */}
      <h2 className="text-black text-lg font-semibold">{name}</h2>

      {/* Pizza Description */}
      <p className="text-gray-600 text-center text-sm">
        {description}
      </p>

      {/* Pizza Price */}
      <p className="text-[#e90028] text-xl font-bold">${price}</p>
    </div>
  );
};

export default PizzaCard;
