import React, { useState } from 'react';
import Dashboard from './Dashboard';
import ManagePizza from './ManagePizza';
import ManageReservation from './ManageReservation';

const AdminPanel = () => {
  const [activeSection, setActiveSection] = useState('dashboard');

  return (
    <div className="min-h-screen flex flex-col">
      {/* Top Bar */}
      <div className="bg-gray-800 text-white p-4 flex justify-between items-center">
        <div className="text-xl font-semibold">Admin Panel</div>
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search..."
            className="p-2 rounded bg-gray-600 placeholder-gray-300 focus:outline-none"
          />
          <button className="bg-red-500 px-4 py-2 rounded">Logout</button>
        </div>
      </div>

      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="w-1/4 bg-gray-800 text-white p-5">
          <ul>
            <li>
              <button
                className={`w-full text-left p-3 mb-2 rounded ${
                  activeSection === 'dashboard' ? 'bg-red-500' : ''
                }`}
                onClick={() => setActiveSection('dashboard')}
              >
                Dashboard
              </button>
            </li>
            <li>
              <button
                className={`w-full text-left p-3 mb-2 rounded ${
                  activeSection === 'pizza' ? 'bg-red-500' : ''
                }`}
                onClick={() => setActiveSection('pizza')}
              >
                Manage Pizza
              </button>
            </li>
            <li>
              <button
                className={`w-full text-left p-3 mb-2 rounded ${
                  activeSection === 'reservations' ? 'bg-red-500' : ''
                }`}
                onClick={() => setActiveSection('reservations')}
              >
                Manage Reservations
              </button>
            </li>
          </ul>
        </aside>

        {/* Content Area */}
        <main className="w-3/4 p-10 bg-gray-100">
          {activeSection === 'dashboard' && <Dashboard />}
          {activeSection === 'pizza' && <ManagePizza />}
          {activeSection === 'reservations' && <ManageReservation />}
        </main>
      </div>
    </div>
  );
};

export default AdminPanel;
