import axios from 'axios';

const API_URL = 'http://localhost:4001/piz';

// Get all pizzas (Public route)
export const getPizzas = async () => {
  try {
    const response = await axios.get('http://localhost:4001/piz/pizzas');
    console.log(response.data)

    return response.data;
  } catch (error) {
    console.error('Error fetching pizzas:', error);
    throw error;
  }
};

// Add a pizza (Admin protected route)
export const addPizza = async (pizzaData, token) => {
  try {
    const response = await axios.post(`${API_URL}/addpizza`, pizzaData, {
      headers: {
        Authorization: `Bearer ${token}`, 
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error adding pizza:', error);
    throw error;
  }
};

// Update a pizza (Admin protected route)
export const updatePizza = async (pizzaId, updatedData, token) => {
  try {
    const response = await axios.put(`${API_URL}/updatepizza/${pizzaId}`, updatedData, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error updating pizza:', error);
    throw error;
  }
};

// Delete a pizza (Admin protected route)
export const deletePizza = async (pizzaId, token) => {
  try {
    const response = await axios.delete(`${API_URL}/delpizza/${pizzaId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error deleting pizza:', error);
    throw error;
  }
};
