import React, { useState, useEffect ,} from 'react';
import { } from '../context/PizzaContext';


const Dashboard = () => {
  const { pizzas, fetchPizzas } = usePizza(); 


  const [reservations, setReservations] = useState([
    { id: 1, name: 'John Doe', date: '2024-10-13', status: 'Pending' },
    { id: 2, name: 'Jane Smith', date: '2024-10-13', status: 'Confirmed' },
    { id: 3, name: 'Bob Brown', date: '2024-10-12', status: 'Pending' },
    // Add more reservation data here
  ]);

  const today = new Date().toISOString().split('T')[0];
  const todaysReservations = reservations.filter(
    (reservation) => reservation.date === today
  );
  const pendingReservations = reservations.filter(
    (reservation) => reservation.status === 'Pending'
  ).length;

  useEffect(() => {
    fetchPizzas(); // Fetch pizzas when the component mounts
  }, [fetchPizzas]);


  return (
    <div>
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      {/* Summary Boxes */}
      <div className="grid grid-cols-2 gap-4 mb-8">
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Total Pizza Menu</h3>
          <p className="text-4xl">{pizzas.length}</p>
        </div>
        <div className="bg-white p-6 shadow rounded-lg">
          <h3 className="text-xl font-semibold">Pending Reservations</h3>
          <p className="text-4xl">{pendingReservations}</p>
        </div>
      </div>

      {/* Today's Reservations Table */}
      <div className="bg-white p-6 shadow rounded-lg">
        <h3 className="text-xl font-semibold mb-4">Today's Reservations</h3>
        <table className="w-full text-left border-collapse">
          <thead>
            <tr>
              <th className="border-b p-2">Name</th>
              <th className="border-b p-2">Date</th>
              <th className="border-b p-2">Status</th>
            </tr>
          </thead>
          <tbody>
            {todaysReservations.map((reservation) => (
              <tr key={reservation.id}>
                <td className="border-b p-2">{reservation.name}</td>
                <td className="border-b p-2">{reservation.date}</td>
                <td className="border-b p-2">{reservation.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
