import React, { useState, useEffect } from 'react';
import { FaTachometerAlt, FaPizzaSlice, FaCalendarAlt, FaSearch, FaSignOutAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import Dashboard from './Dashboard';
import ManagePizza from './ManagePizza';
import ManageReservation from './ManageReservation';

const AdminPanel = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [openReservations, setOpenReservations] = useState(false); // State to manage visibility of reservations
  const navigate = useNavigate(); 

  useEffect(() => {
    const message = localStorage.getItem('loginMessage');
    
    if (message) {
      toast.success(message);
      localStorage.removeItem('loginMessage'); // Clear message after displaying
    }
    
    // Redirect if token is not present
    if (!localStorage.getItem('token')) {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [navigate]);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear the token
    navigate('/login'); // Redirect to the login page
    toast.info('Logged out successfully!'); // Show logout success message
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Top Bar */}
      <div className="bg-gray-800 text-white p-4 flex justify-between items-center">
        <div className="text-xl font-semibold">Admin Panel</div>
        <div className="flex items-center space-x-4">
          <div className="relative">
            <input
              type="text"
              placeholder="Search..."
              className="p-2 rounded bg-gray-600 placeholder-gray-300 focus:outline-none"
            />
            <FaSearch className="absolute top-2 right-2 text-gray-400" />
          </div>
          <button onClick={handleLogout} className="bg-red-500 px-4 py-2 rounded flex items-center space-x-2">
            <FaSignOutAlt />
            <span>Logout</span>
          </button>
        </div>
      </div>

      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="w-1/5 bg-gray-800 text-white flex flex-col py-6 space-y-6">
          <button
            className={`w-full flex items-center space-x-2 px-4 py-3 hover:bg-red-500 ${activeSection === 'dashboard' ? 'bg-red-500' : ''}`}
            onClick={() => setActiveSection('dashboard')}
          >
            <FaTachometerAlt className="text-lg" />
            <span>Dashboard</span>
          </button>

          <button
            className={`w-full flex items-center space-x-2 px-4 py-3 hover:bg-red-500 ${activeSection === 'pizza' ? 'bg-red-500' : ''}`}
            onClick={() => setActiveSection('pizza')}
          >
            <FaPizzaSlice className="text-lg" />
            <span>Manage Pizza</span>
          </button>

          <div>
            <button
              className={`w-full flex items-center space-x-2 px-4 py-3 hover:bg-red-500 ${activeSection === 'reservations' ? 'bg-red-500' : ''}`}
              onClick={() => {
                setActiveSection('reservations');
                setOpenReservations(!openReservations); // Toggle the sub-menu
              }}
            >
              <FaCalendarAlt className="text-lg" />
              <span>Manage Reservations</span>
            </button>

            {openReservations && (
              <div className="pl-4">
                <h4 className="text-sm font-medium mt-2">Upcoming Reservations</h4>
                <ul className="space-y-1">
                  <li>
                    <NavLink
                      to="/active-reservations"
                      className={({ isActive }) =>
                        "block transition duration-150 truncate " + (isActive ? "text-violet-500" : "text-gray-500/90 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200")
                      }
                    >
                      <span className="text-sm">Active Reservations</span>
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      to="/upcoming-reservations"
                      className={({ isActive }) =>
                        "block transition duration-150 truncate " + (isActive ? "text-violet-500" : "text-gray-500/90 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-200")
                      }
                    >
                      <span className="text-sm">Upcoming Reservations</span>
                    </NavLink>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </aside>

        {/* Content Area */}
        <main className="w-full p-10 bg-gray-100">
          {activeSection === 'dashboard' && <Dashboard />}
          {activeSection === 'pizza' && <ManagePizza />}
          {activeSection === 'reservations' && <ManageReservation />}
        </main>
      </div>
      <ToastContainer position="top-right" autoClose={5000} hideProgressBar={false} />
    </div>
  );
};

export default AdminPanel;
