import React, { useState } from 'react';
import {  FaArrowRight } from 'react-icons/fa';

const Reservation = () => {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [guests, setGuests] = useState(1);
  const [contactNumber, setContactNumber] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission logic here (e.g., API call)
    console.log({ date, time, guests, contactNumber, message });
    // Reset the form
    setDate('');
    setTime('');
    setGuests(1);
    setContactNumber('');
    setMessage('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-4 flex space-x-4">
        <div className="flex-1">
          <label htmlFor="date" className="block text-sm font-medium text-gray-700">
            Select Date
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
            required
          />
        </div>
        <div className="flex-1">
          <label htmlFor="time" className="block text-sm font-medium text-gray-700">
            Select Time
          </label>
          <input
            type="time"
            id="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
            required
          />
        </div>
      </div>
      <div className="mb-4 flex space-x-4">
        <div className="flex-1">
          <label htmlFor="contact" className="block text-sm font-medium text-gray-700">
            Contact Number
          </label>
          <input
            type="tel"
            id="contact"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
            required
          />
        </div>
        <div className="flex-1">
          <label htmlFor="guests" className="block text-sm font-medium text-gray-700">
            Total Guests
          </label>
          <input
            type="number"
            id="guests"
            value={guests}
            onChange={(e) => setGuests(e.target.value)}
            className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
            required
            min="1"
          />
        </div>
      </div>
      <div className="mb-4">
        <label htmlFor="message" className="block text-sm font-medium text-gray-700">
          Write Message
        </label>
        <textarea
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500"
          rows="3"
        ></textarea>
      </div>
      <button
        type="submit"
        className="bg-[#e90028] flex items-center justify-center space-x-2 text-white px-3 md:px-4 py-2 rounded-sm font-semibold hover:bg-red-700">
      >
        Make A Reservation
        <FaArrowRight className="w-3 sm:w-4 h-3 sm:h-4" />

      </button>
    </form>
  );
};

export default Reservation;
