// src/App.js
import React, { useEffect } from 'react';
import { PizzaProvider, usePizza } from './context/PizzaContext';
import PizzaList from './components/PizzaList'; // Example component to display pizzas

const App = () => {
  return (
    <PizzaProvider>
      <PizzaList />
    </PizzaProvider>
  );
};

export default App;
