import React, { useState } from 'react';
import { usePizza } from '../context/PizzaContext';


const ManagePizza = () => {
    const { state, fetchPizzas } = usePizza();
    const { pizzas, loading, error } = state;

    useEffect(() => {
        fetchPizzas();
      }, [fetchPizzas]);
    
      if (loading) return <Loading />;
      if (error) return <Error />;

  return (
    <div>
      <h2 className="text-3xl mb-4">Manage Pizza</h2>
      
      {/* Add New Pizza */}
      <div className="mb-6">
        <h3 className="text-xl mb-2">Add New Pizza</h3>
        <input
          className="border p-2 mr-2"
          type="text"
          placeholder="Pizza Name"
          value={newPizza.name}
          onChange={(e) => setNewPizza({ ...newPizza, name: e.target.value })}
        />
        <input
          className="border p-2 mr-2"
          type="number"
          placeholder="Price"
          value={newPizza.price}
          onChange={(e) => setNewPizza({ ...newPizza, price: e.target.value })}
        />
        <button onClick={addPizza} className="bg-red-500 text-white p-2 rounded">
          Add Pizza
        </button>
      </div>

      {/* List of Pizzas */}
      <div>
        <h3 className="text-xl mb-2">Pizza List</h3>
        <ul>
          {pizzas.map((pizza) => (
            <li key={pizza.id} className="flex justify-between mb-2">
              <span>
                {pizza.name} - ${pizza.price}
              </span>
              <button
                onClick={() => handleDelete(pizza.id)}
                className="bg-red-500 text-white p-1 rounded"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ManagePizza;
