import { usePizza } from '../context/PizzaContext';
import Loading from '../components/Loading';
import Error from './Error';
import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash } from 'react-icons/fa'; // Import icons

const ManagePizza = () => {
  const { state, fetchPizzas, addPizza, handleDelete, handleEdit } = usePizza();
  const { pizzas, loading, error } = state;
  
  const [newPizza, setNewPizza] = useState({ name: '', price: '', description: '', image: '' });

  useEffect(() => {
    fetchPizzas();
  }, [fetchPizzas]);

  if (loading) return <Loading />;
  if (error) return <Error />;

  return (
    <div>
      <h2 className="text-3xl mb-4">Manage Pizza</h2>
      
      {/* Add New Pizza */}
      <div className="mb-6">
        <h3 className="text-xl mb-2">Add New Pizza</h3>
        <input
          className="border p-2 mr-2 mb-2"
          type="text"
          placeholder="Pizza Name"
          value={newPizza.name}
          onChange={(e) => setNewPizza({ ...newPizza, name: e.target.value })}
        />
        <input
          className="border p-2 mr-2 mb-2"
          type="number"
          placeholder="Price"
          value={newPizza.price}
          onChange={(e) => setNewPizza({ ...newPizza, price: e.target.value })}
        />
        <input
          className="border p-2 mr-2 mb-2"
          type="text"
          placeholder="Description"
          value={newPizza.description}
          onChange={(e) => setNewPizza({ ...newPizza, description: e.target.value })}
        />
        <input
          className="border p-2 mr-2 mb-2"
          type="text"
          placeholder="Image URL"
          value={newPizza.image}
          onChange={(e) => setNewPizza({ ...newPizza, image: e.target.value })}
        />
        <button onClick={() => addPizza(newPizza)} className="bg-red-500 text-white p-2 rounded">
          Add Pizza
        </button>
      </div>

      {/* Pizza Table */}
      <div>
        <h3 className="text-xl mb-2">Pizza List</h3>
        <table className="min-w-full bg-white border">
          <thead>
            <tr className="bg-gray-100 text-left">
              <th className="py-2 px-4 border-b">Pizza Name</th>
              <th className="py-2 px-4 border-b">Price</th>
              <th className="py-2 px-4 border-b">Description</th>
              <th className="py-2 px-4 border-b">Image</th>
              <th className="py-2 px-4 border-b">Actions</th>
            </tr>
          </thead>
          <tbody>
            {pizzas.map((pizza) => (
              <tr key={pizza.id} className="border-b">
                <td className="py-2 px-4">{pizza.Name}</td>
                <td className="py-2 px-4">${pizza.Price}</td>
                <td className="py-2 px-4">{pizza.Description}</td>
                <td className="py-2 px-4">
                  <img src={pizza.Image} alt={pizza.Name} className="w-16 h-16 object-cover" />
                </td>
                <td className=" justify-normal py-2 px-4 flex space-x-2">
                  <button
                    onClick={() => handleEdit(pizza.id)}
                    className="text-black text-al"
                  >
                    <FaEdit />
                  </button>
                  <button
                    onClick={() => handleDelete(pizza.id)}
                    className="text-[#e90028]"
                  >
                    <FaTrash />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManagePizza;
