import React, { useState } from 'react';
import { usePizza } from '../context/PizzaContext'; // Adjust the path as necessary

const SearchModal = ({ isOpen, onClose }) => {
    const { state, fetchPizzas } = usePizza();
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredPizzas, setFilteredPizzas] = useState([]);

    const handleSearch = () => {
        if (!searchTerm) return;

        // Filter pizzas based on the search term (case insensitive)
        const filtered = state.pizzas.filter(pizza =>
            pizza.Name.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredPizzas(filtered);
    };

    return (
        isOpen && (
            <div className="fixed inset-0 z-50 flex items-center justify-center bg-[#010f1c] bg-opacity-80">
                <div className="bg-white rounded-lg shadow-lg p-6 w-96">
                    <h2 className="text-2xl font-bold mb-4 text-center text-[#e90028]">Search Pizzas</h2>
                    <input
                        type="text"
                        placeholder="Search for a pizza..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-full p-2 border border-gray-300 rounded mb-4 focus:outline-none focus:ring-2 focus:ring-[#e90028]"
                    />
                    <div className="flex ">
                        <button
                            onClick={() => {
                                handleSearch();
                                fetchPizzas(); // Optional: Fetch pizzas if not already fetched
                            }}
                            className="bg-[#e90028] text-white rounded px-4 py-2 transition duration-200 ease-in-out hover:bg-[#ff2c4a] shadow-md"
                        >
                            Search
                        </button>
                        {/* <button
                            onClick={onClose}
                            className="bg-gray-500 text-white rounded px-4 py-2 transition duration-200 ease-in-out hover:bg-gray-700 shadow-md"
                        >
                            Close
                        </button> */}
                    </div>

                    <div className="mt-4">
                        {filteredPizzas.length > 0 ? (
                            <ul className="max-h-48 overflow-y-auto">
                                {filteredPizzas.map((pizza) => (
                                    <li key={pizza.id} className="py-1 hover:bg-[#f0f0f0] rounded transition duration-200 ease-in-out">
                                        {pizza.Name}
                                    </li>
                                ))}
                            </ul>
                        ) : (
                            <p className="text-center text-gray-600">No results found</p>
                        )}
                    </div>
                </div>
            </div>
        )
    );
};

export default SearchModal;
